        for (var i = 0; i < formatArray.length; i = i + 1){
            var v = parseInt(val[i], 10);
            if (formatArray[i] === 'm' && !isNaN(v)) {
                res = v;
            }
        }
        return ((this.monthMaxDays[res] > this.minMaxDate.maxDay) ? this.minMaxDate.maxDay : this.monthMaxDays[res]);
        
